const HealthChecker = global.get('HealthChecker');

if (!HealthChecker) {
    node.error('HealthChecker not initialized');
    return null;
}

const checkType = msg.payload;
node.status({fill: 'blue', shape: 'dot', text: `Running ${checkType} check...`});

(async () => {
    try {
        const healthChecker = new HealthChecker();
        const healthStatus = await healthChecker.performFullHealthCheck();

        // Store health status in global context
        global.set('lastHealthStatus', healthStatus);

        // Update node status
        const statusColor = healthStatus.overall === 'healthy' ? 'green' : 
                           healthStatus.overall === 'degraded' ? 'yellow' : 'red';

        node.status({
            fill: statusColor, 
            shape: 'dot', 
            text: `${healthStatus.overall} (${healthStatus.healthyServices}/${healthStatus.totalServices})`
        });

        // Send results to different outputs based on status
        const healthyMsg = { ...msg, payload: healthStatus, topic: 'health-status' };
        const unhealthyServices = Object.entries(healthStatus.services)
            .filter(([name, service]) => service.status === 'unhealthy')
            .map(([name]) => name);

        if (healthStatus.overall === 'healthy') {
            node.send([healthyMsg, null, null]);
        } else if (healthStatus.overall === 'degraded') {
            node.send([null, healthyMsg, null]);
        } else {
            const unhealthyMsg = { 
                ...msg, 
                payload: { 
                    healthStatus, 
                    unhealthyServices 
                }, 
                topic: 'unhealthy-services' 
            };
            node.send([null, null, unhealthyMsg]);
        }

    } catch (error) {
        node.error(`Health check failed: ${error.message}`);
        node.status({fill: 'red', shape: 'ring', text: 'Check failed'});
    }
})();

return null;

const HealthChecker = global.get('HealthChecker');

if (!HealthChecker) {
    node.error('HealthChecker not initialized');
    return null;
}

const { healthStatus, unhealthyServices } = msg.payload;

node.status({fill: 'yellow', shape: 'dot', text: `Healing ${unhealthyServices.length} services...`});

(async () => {
    try {
        const healthChecker = new HealthChecker();
        const healingResult = await healthChecker.autoHeal();

        if (healingResult) {
            node.status({fill: 'green', shape: 'dot', text: 'Auto-healing successful'});
            node.send({ 
                ...msg, 
                payload: { 
                    success: true, 
                    healedServices: unhealthyServices,
                    timestamp: new Date().toISOString()
                },
                topic: 'healing-success'
            });
        } else {
            node.status({fill: 'red', shape: 'dot', text: 'Auto-healing failed'});
            node.send({ 
                ...msg, 
                payload: { 
                    success: false, 
                    failedServices: unhealthyServices,
                    timestamp: new Date().toISOString()
                },
                topic: 'healing-failed'
            });
        }

    } catch (error) {
        node.error(`Auto-healing failed: ${error.message}`);
        node.status({fill: 'red', shape: 'ring', text: 'Healing error'});
    }
})();

return null;

const HealthChecker = global.get('HealthChecker');

if (!HealthChecker) {
    msg.payload = {
        error: 'HealthChecker not initialized',
        status: 500
    };
    return msg;
}

(async () => {
    try {
        const healthChecker = new HealthChecker();
        const healthStatus = await healthChecker.performFullHealthCheck();

        msg.payload = {
            status: healthStatus.overall === 'healthy' ? 200 : 
                   healthStatus.overall === 'degraded' ? 207 : 503,
            data: healthStatus
        };

        node.send(msg);

    } catch (error) {
        msg.payload = {
            error: error.message,
            status: 500
        };
        node.send(msg);
    }
})();

return null;

const healthStatus = global.get('lastHealthStatus');

if (healthStatus) {
    const message = `üöÄ SolarFactsNW startup complete!\n` +
                   `Overall status: ${healthStatus.overall}\n` +
                   `Services: ${healthStatus.healthyServices}/${healthStatus.totalServices} healthy\n` +
                   `Startup time: ${new Date().toLocaleString()}`;

    node.status({fill: 'green', shape: 'dot', text: 'Startup complete'});

    msg.payload = {
        message,
        healthStatus,
        timestamp: new Date().toISOString()
    };
} else {
    msg.payload = {
        message: '‚ö†Ô∏è SolarFactsNW started but health status unknown',
        timestamp: new Date().toISOString()
    };
}

return msg;
